import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

function App() {
  // Fetch devices from backend
  const fetchDevices = async () => {
    try {
      const res = await axios.get('http://localhost:8000/devices');
      setDevices(res.data);
    } catch (e) {
      alert('Failed to fetch devices: ' + (e.response?.data?.detail || e.message));
    }
  };
    const [unlockModal, setUnlockModal] = useState({ open: false, drive: '', method: 'password', value: '' });
    const [consent, setConsent] = useState(false);
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState('');
    const [method, setMethod] = useState('auto');
    const [progress, setProgress] = useState([]);
    const [erasing, setErasing] = useState(false);
    const [certificate, setCertificate] = useState(null);
    const [checklist, setChecklist] = useState({ adb: false, connected: false, encrypted: false });
    const [androidEncryption, setAndroidEncryption] = useState(null);
    const [winBitLocker, setWinBitLocker] = useState({ status: null, unlocking: false });
    const wsRef = useRef(null);

    // WebSocket cleanup on unmount
    useEffect(() => {
      return () => {
        if (wsRef.current) wsRef.current.close();
      };
    }, []);
  const startErase = async () => {
    setErasing(true);
    setProgress([`Started erasing ${selectedDevice} with ${method}`]);
    wsRef.current = new window.WebSocket(`ws://localhost:8000/logs/${encodeURIComponent(selectedDevice)}`);
    wsRef.current.onmessage = (event) => {
      setProgress((p) => [...p, event.data]);
      if (event.data.includes('Certificate generated.')) {
        wsRef.current.close();
        setErasing(false);
        fetchCertificate();
      }
    };
    wsRef.current.onerror = () => {
      setProgress((p) => [...p, 'WebSocket error.']);
    };
    try {
      await axios.post('http://localhost:8000/erase', {
        device_id: selectedDevice,
        method,
      });
    } catch (e) {
      setProgress((p) => [...p, 'Erase failed: ' + (e.response?.data?.detail || e.message)]);
      setErasing(false);
      if (wsRef.current) wsRef.current.close();
    }
  };

  const fetchCertificate = async () => {
    try {
      const res = await axios.get('http://localhost:8000/certificate', {
        params: { device_id: selectedDevice, format: 'json' },
      });
      setCertificate(res.data);
    } catch (e) {
      setProgress((p) => [...p, 'Failed to fetch certificate: ' + (e.response?.data?.detail || e.message)]);
    }
  };

  const downloadCert = (format) => {
    const url = `http://localhost:8000/certificate?device_id=${encodeURIComponent(selectedDevice)}&format=${format}`;
    window.open(url, '_blank');
  };

  // Checklist for Android devices
  const isAndroid = devices.find(d => d.id === selectedDevice && d.type.startsWith('Android'));
  const isWindowsDrive = devices.find(d => d.id === selectedDevice && (d.type === 'SSD' || d.type === 'HDD'));

  // Auto-detect Android encryption status (via backend API)
  useEffect(() => {
    if (isAndroid) {
      const serial = selectedDevice.split(':')[1];
      fetch(`http://localhost:8000/android_encryption?serial=${serial}`)
        .then(res => {
          if (!res.ok) throw new Error('Failed to detect encryption');
          return res.json();
        })
        .then(data => setAndroidEncryption(data.encrypted ? 'Encrypted' : 'Not Encrypted'))
        .catch(() => setAndroidEncryption('Unknown'));
    } else {
      setAndroidEncryption(null);
    }
  }, [selectedDevice, isAndroid]);

  // Auto-detect Windows BitLocker status (via backend API)
  useEffect(() => {
    if (isWindowsDrive) {
      fetch(`http://localhost:8000/bitlocker_status?device_id=${encodeURIComponent(selectedDevice)}`)
        .then(res => {
          if (!res.ok) throw new Error('Failed to detect BitLocker status');
          return res.json();
        })
        .then(data => {
          if (data.results && data.results.length > 0) {
            setWinBitLocker({
              status: data.results.map(r => `${r.drive}: ${r.status}`).join(', '),
              unlocking: false,
              details: data.results
            });
          } else {
            setWinBitLocker({ status: 'Unknown', unlocking: false, details: [] });
          }
        })
        .catch(() => setWinBitLocker({ status: 'Error', unlocking: false, details: [] }));
    } else {
      setWinBitLocker({ status: null, unlocking: false, details: [] });
    }
  }, [selectedDevice, isWindowsDrive]);

  if (!consent) {
    return (
      <div style={{ padding: 40 }}>
        <h2>This process will permanently erase selected drives. Proceed?</h2>
        <button onClick={() => { setConsent(true); fetchDevices(); }}>I Understand, Continue</button>
      </div>
    );
  }

  if (certificate) {
    return (
      <div style={{ padding: 40 }}>
        <h2>Erase Complete</h2>
        <pre>{JSON.stringify(certificate, null, 2)}</pre>
        <button onClick={() => downloadCert('pdf')}>Download PDF Certificate</button>
        <button onClick={() => downloadCert('json')}>Download JSON Certificate</button>
        <br />
        <button onClick={() => window.location.reload()}>Start Over</button>
      </div>
    );
  }

  return (
    <div style={{ padding: 40 }}>
      <h2>Select Device to Erase</h2>
      <select value={selectedDevice} onChange={e => setSelectedDevice(e.target.value)}>
        <option value="">-- Select --</option>
        {devices.map(d => (
          <option key={d.id} value={d.id}>
            {d.model} [{d.type}] ({d.size}){d.serial ? ` [Serial: ${d.serial}]` : ''}
          </option>
        ))}
      </select>

      {isAndroid && (
        <div style={{ margin: '20px 0', padding: '10px', border: '1px solid #ccc', borderRadius: 8, background: '#f9f9f9' }}>
          <h3>Android Device Checklist</h3>
          <label>
            <input type="checkbox" checked={checklist.adb} onChange={e => setChecklist(c => ({ ...c, adb: e.target.checked }))} />
            Enable USB Debugging (ADB) on device
          </label><br />
          <label>
            <input type="checkbox" checked={checklist.connected} onChange={e => setChecklist(c => ({ ...c, connected: e.target.checked }))} />
            Device is connected and authorized (shows in 'adb devices')
          </label><br />
          <label>
            <input type="checkbox" checked={checklist.encrypted} onChange={e => setChecklist(c => ({ ...c, encrypted: e.target.checked }))} />
            Device storage is encrypted (recommended for secure erase)
          </label>
          <div style={{ color: '#888', fontSize: 13, marginTop: 8 }}>
            <b>Note:</b> Factory reset is only non-recoverable if device is encrypted.<br />
            Encryption status: <b>{androidEncryption === null ? 'Detecting...' : androidEncryption}</b><br />
            You can check encryption status in device settings.<br />
          </div>
        </div>
      )}

      {isWindowsDrive && (
        <div style={{ margin: '20px 0', padding: '10px', border: '1px solid #ccc', borderRadius: 8, background: '#f9f9f9' }}>
          <h3>Windows Drive Checklist</h3>
          <div>
            BitLocker status: <b>{winBitLocker.status === null ? 'Detecting...' : winBitLocker.status}</b>
            {/* Show unlock button for each locked drive */}
            {winBitLocker.details && winBitLocker.details.map(r => (
              r.status === 'Locked' ? (
                <button
                  key={r.drive}
                  style={{ marginLeft: 10 }}
                  disabled={winBitLocker.unlocking}
                  onClick={() => setUnlockModal({ open: true, drive: r.drive, method: 'password', value: '' })}
                >Unlock BitLocker ({r.drive})</button>
              ) : null
            ))}
  {/* BitLocker Unlock Modal */}
  {unlockModal.open && (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.4)', zIndex: 9999 }}>
      <div style={{ background: '#fff', padding: 30, borderRadius: 8, maxWidth: 400, margin: '100px auto', boxShadow: '0 2px 8px #0003' }}>
        <h3>Unlock BitLocker ({unlockModal.drive})</h3>
        <label>Unlock method:</label>
        <select value={unlockModal.method} onChange={e => setUnlockModal(m => ({ ...m, method: e.target.value, value: '' }))}>
          <option value="password">Password</option>
          <option value="recovery_key">Recovery Key</option>
          <option value="numerical_password">Numerical Password</option>
          <option value="key_file">Key File (.BEK)</option>
        </select>
        <br /><br />
        <label>
          {unlockModal.method === 'key_file' ? 'Key File Path:' : 'Value:'}
          <input
            type="text"
            value={unlockModal.value}
            onChange={e => setUnlockModal(m => ({ ...m, value: e.target.value }))}
            style={{ width: '100%', marginTop: 8 }}
            placeholder={unlockModal.method === 'key_file' ? 'C:\path\to\keyfile.BEK' : 'Enter value'}
          />
        </label>
        <br /><br />
        <button
          disabled={winBitLocker.unlocking}
          onClick={async () => {
            // Input sanitization
            if (!unlockModal.value || unlockModal.value.length < 3) {
              alert('Please enter a valid value.');
              return;
            }
            setWinBitLocker(w => ({ ...w, unlocking: true }));
            const body = { device_id: unlockModal.drive };
            body[unlockModal.method] = unlockModal.value;
            try {
              const res = await fetch('http://localhost:8000/bitlocker_unlock', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(body)
              });
              const data = await res.json();
              if (res.ok && data.status === 'Unlocked') {
                setWinBitLocker(w => ({ ...w, status: w.status.replace(`${unlockModal.drive}: Locked`, `${unlockModal.drive}: Unlocked`), unlocking: false }));
              } else {
                alert('Unlock failed: ' + (data.error || data.output || 'Unknown error'));
                setWinBitLocker(w => ({ ...w, unlocking: false }));
              }
            } catch (e) {
              alert('Unlock failed: ' + e.message);
              setWinBitLocker(w => ({ ...w, unlocking: false }));
            }
            setUnlockModal({ open: false, drive: '', method: 'password', value: '' });
          }}
        >{winBitLocker.unlocking ? 'Unlocking...' : 'Unlock'}</button>
        <button style={{ marginLeft: 10 }} onClick={() => setUnlockModal({ open: false, drive: '', method: 'password', value: '' })}>Cancel</button>
      </div>
    </div>
  )}
          </div>
          <div style={{ color: '#888', fontSize: 13, marginTop: 8 }}>
            <b>Note:</b> Secure erase is only possible if BitLocker is unlocked.<br />
          </div>
        </div>
      )}

      <h3>Erase Method</h3>
      <select value={method} onChange={e => setMethod(e.target.value)}>
        <option value="auto">Auto Secure Erase</option>
        <option value="multi-pass">Multi-pass overwrite</option>
        <option value="crypto-erase">Crypto-erase</option>
      </select>
      <br /><br />
      <button
        disabled={!selectedDevice || erasing || (isAndroid && !(checklist.adb && checklist.connected && checklist.encrypted))}
        onClick={startErase}
      >{erasing ? 'Erasing...' : 'Erase'}</button>
      <div style={{ marginTop: 30 }}>
        <h4>Progress</h4>
        <pre>{progress.join('\n')}</pre>
      </div>

  // ...existing code...

    </div>
  );
}

export default App;
