import React, { useState } from 'react';
import { FiMoon, FiSun, FiArrowRight, FiCheck } from 'react-icons/fi';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(LineElement, PointElement, LinearScale, CategoryScale, Tooltip, Legend);

const LandingPage = ({ onStart }) => {
  const [theme, setTheme] = useState('dark');
  const [email, setEmail] = useState('');
  const toggleTheme = () => setTheme(t=> t==='dark' ? 'light':'dark');

  const chartData = {
    labels: ['100','200','300','400','500','600','700','800'],
    datasets:[
      { label:'Network activity', data:[120,180,160,210,240,260,300,340], borderColor:'#3b82f6', backgroundColor:'rgba(59,130,246,0.15)', fill:true, tension:.35, pointRadius:0 },
      { label:'Transaction volume', data:[80,130,190,170,220,210,260,400], borderColor:'#fbbf24', backgroundColor:'rgba(251,191,36,0.15)', fill:true, tension:.35, pointRadius:0 }
    ]
  };
  const chartOptions = {
    responsive:true,
    maintainAspectRatio:false,
    plugins:{ legend:{ display:false }, tooltip:{ mode:'index', intersect:false } },
    scales:{
      x:{ ticks:{ color: theme==='dark'? '#64748b':'#475569', maxRotation:0 }, grid:{ color: theme==='dark'? 'rgba(255,255,255,0.05)':'rgba(0,0,0,0.05)' }},
      y:{ ticks:{ color: theme==='dark'? '#64748b':'#475569' }, grid:{ color: theme==='dark'? 'rgba(255,255,255,0.05)':'rgba(0,0,0,0.05)' }}
    }
  };

  return (
    <div className={`landing-root ${theme}`}>      
      <nav className="landing-nav">
        <div className="landing-logo">CertiWipe</div>
        <div className="landing-links">
          <button>Solutions</button>
          <button>Technology</button>
          <button>Community</button>
          <button>Resources</button>
        </div>
        <div className="landing-actions">
          <button aria-label="Toggle theme" onClick={toggleTheme} className="toggle-theme">{theme==='dark'? <FiSun/>:<FiMoon/>}</button>
          <button className="cta-ghost" onClick={onStart}>Get started</button>
        </div>
      </nav>

      <header className="hero">
        <h1 className="hero-title">Shaping the Future of <span className="grad-accent">Secure Data Erasure</span></h1>
        <p className="hero-sub">Cryptographic, eco‑aware, auditable wiping for modern hardware fleets. Trust, transparency and sustainability in one workflow.</p>
        <div className="hero-form">
          <input type="email" placeholder="Your email address" value={email} onChange={e=>setEmail(e.target.value)} />
          <button onClick={onStart} className="cta-solid">Get started <FiArrowRight/></button>
        </div>
      </header>

      <section className="feature-grid">
        <div className="fg-card fg-tags">
          {['#EntropyProof','#EcoAware','#AuditChain','#ZeroTrust','#Merkle','#DualApproval','#HardwareMap'].map(t => (
            <span key={t}>{t}</span>
          ))}
        </div>
        <div className="fg-card fg-icons">
          <div className="icon-pod">🔐</div>
          <div className="icon-pod">⚡</div>
          <div className="icon-pod">🌱</div>
          <div className="icon-pod">🧪</div>
        </div>
        <div className="fg-card fg-toggle">
          <div className="toggle-question">Do you prefer cutting‑edge assurance over enhanced speed?</div>
          <div className="toggle-buttons">
            <button className="yes"><FiCheck/> Yes</button>
            <button className="no" onClick={onStart}>Start now</button>
          </div>
        </div>
        <div className="fg-card fg-chart">
          <div className="mini-stats">
            <div>
              <span className="label">Energy Saved</span>
              <span className="value">38.48 kWh</span>
            </div>
            <div>
              <span className="label">CO₂ Avoided</span>
              <span className="value">14.02 kg</span>
            </div>
          </div>
          <div className="chart-wrapper"><Line data={chartData} options={chartOptions} height={180} /></div>
        </div>
      </section>

      <footer className="landing-brands">
        {['IBM','ripple','coinbase','binance','Digital8','THERMADYNE','blinto','IBIDEN'].map(b => <span key={b}>{b}</span>)}
      </footer>
    </div>
  );
};

export default LandingPage;
